mapper.select:
SELECT post.*,f.name AS forum_name,avatar,level,IFNULL(username,'用户已注销') AS username,
(IF(pl.id IS null, false, true)) AS is_like
FROM (SELECT * FROM collection WHERE uid=#{uid}) pl
LEFT JOIN post ON post.pid = pl.pid AND pl.uid = #{uid}
LEFT JOIN user u ON pl.uid = u.uid
LEFT JOIN forum_user fu ON post.fid = fu.fid AND pl.uid = fu.uid
LEFT JOIN forum f ON post.fid = f.fid
ORDER BY last_date DESC

    LIMIT #{pageSize}


    OFFSET #{offset}
