mapper.select:
SELECT fpost.pid,fpost.uid,IFNULL(username,'用户已注销') AS username, avatar, level,post_time,like_num,reply_num,title,content,picture, (CASE WHEN #{uid} IS null THEN false
WHEN pl.id IS null THEN false
ELSE true END) AS is_like FROM (SELECT * FROM post WHERE fid=#{fid}) fpost LEFT JOIN user u ON fpost.uid = u.uid LEFT JOIN forum_user fu ON fpost.fid = fu.fid AND fpost.uid = fu.uid
LEFT JOIN post_like pl ON fu.uid = pl.uid AND pl.uid = #{uid}
ORDER BY
post_time reply_num last_date
DESC
LIMIT #{pageSize}
OFFSET #{offset}

SELECT fid,name,icon,follow_num,false AS is_like FROM forum ORDER BY follow_num DESC

    LIMIT #{pageSize}


    OFFSET #{offset}

SELECT forum.fid,name,icon,follow_num,IFNULL(is_like,false) AS is_like FROM forum LEFT JOIN
    (SELECT fid,is_like FROM forum_user WHERE uid=#{uid})AS user_forum
ON forum.fid=user_forum.fid ORDER BY follow_num DESC

    LIMIT #{pageSize}


    OFFSET #{offset}

mapper.delete:
DELETE FROM reply WHERE fid=#{id};
DELETE FROM post WHERE fid=#{id};
DELETE FROM forum WHERE fid=#{id};
