mapper.select:
SELECT r.rid, r.content, r.floor_num, r.like_num, r.post_time, r.reply_num, r.uid, IFNULL(username,'用户已注销') AS username, u.avatar, fu.level, (CASE WHEN #{uid} IS null THEN false
WHEN rl.id IS null THEN false
ELSE true END) AS is_like FROM (SELECT * FROM reply WHERE pid=#{pid} AND is_floor=true) AS r LEFT JOIN (SELECT uid, username,avatar FROM user) u ON r.uid=u.uid LEFT JOIN forum_user fu ON fu.uid = u.uid AND fu.fid=r.fid
LEFT JOIN reply_like rl ON r.rid = rl.rid AND rl.uid = #{uid}
ORDER BY
floor_num floor_num DESC reply_num DESC
LIMIT #{pageSize}
OFFSET #{offset}

mapper.update:
UPDATE post SET reply_num=


        reply_num-1


        reply_num-#{num}


WHERE pid=#{pid}
