mapper.select:
SELECT uid,nickname,avatar,intro,fans_num
FROM user WHERE fans_num>=0 ORDER BY fans_num DESC

    LIMIT #{pageSize}


    OFFSET #{offset}

SELECT fpost.*,f.name AS forum_name,avatar,IFNULL(nickname,'用户已注销') AS nickname, (CASE WHEN #{uid} IS null THEN false
WHEN pl.id IS null THEN false
ELSE true END) AS is_like, IFNULL(level,1)AS level FROM (SELECT * FROM post
uid=#{uid}
reading_num>=0 AND like_num>=0 ORDER BY reading_num,like_num LIMIT 10000
) fpost LEFT JOIN user u ON fpost.uid = u.uid LEFT JOIN forum_user fu ON fpost.fid = fu.fid AND fpost.uid = fu.uid LEFT JOIN forum f ON fpost.fid = f.fid
LEFT JOIN post_like pl ON fpost.pid = pl.pid AND pl.uid = #{uid}
ORDER BY
post_time reply_num DESC RAND() post_time DESC
LIMIT #{pageSize}
OFFSET #{offset}

mapper.delete:
DELETE FROM user WHERE uid=#{id};
DELETE FROM collection WHERE uid=#{id};
DELETE FROM forum_user WHERE uid=#{id};
