mapper.select:
SELECT uid,nickname,avatar,intro,fans_num
FROM user ORDER BY fans_num DESC

    LIMIT #{pageSize}


    OFFSET #{offset}

SELECT fpost.*,f.name AS forum_name,avatar,level,IFNULL(username,'用户已注销') AS username, (IF(pl.id IS null, false, true)) AS is_like FROM (SELECT * FROM post WHERE uid=#{uid}) fpost LEFT JOIN user u ON fpost.uid = u.uid LEFT JOIN forum_user fu ON fpost.fid = fu.fid AND fpost.uid = fu.uid LEFT JOIN forum f ON fpost.fid = f.fid LEFT JOIN post_like pl ON fpost.pid = pl.pid AND pl.uid = #{uid} ORDER BY
post_time reply_num last_date
DESC
LIMIT #{pageSize}
OFFSET #{offset}

mapper.delete:
DELETE FROM user WHERE uid=#{id};
DELETE FROM collection WHERE uid=#{id};
DELETE FROM forum_user WHERE uid=#{id};
